import { IntegrationDefinition } from "../types";
import { defaultSettings, settingsSchema } from "./settings";
export * from "./types";
export { AI21Executor } from "./executor";

const integration: IntegrationDefinition = {
  id: "ai21",
  name: "AI21",
  provider: "AI21",
  iconBase64:
    "data:image/png;base64,",
  settingsSchema,
  defaultSettings,
  consumeInstructionsFn: (
    promptName: string,
    variables: Record<string, string>
  ) => {
    let codeBlock = "";

    codeBlock += `import { Pezzo } from "@pezzo/client";
import { AI21Executor } from "@pezzo/integrations";

// Initialize the Pezzo client
const pezzo = new Pezzo({
  serverUrl: "http://localhost:3000",
  apiKey: "...", // <-- Environment API key
});

// Initialize the AI21 client
const ai21 = new AI21Executor(pezzo, {
  apiKey: "..." // <-- Your AI21 API key
});

// Run prompt
const { result } = await ai21.run('${promptName}', {\n`;

    Object.entries(variables).forEach(([key]) => {
      codeBlock += `  ${key}: '...'\n`;
    });

    codeBlock += "});";

    return codeBlock;
  },
};

export default integration;
